const API_BASE_URL = "http://localhost:8000/api";
const AUTH_BASE_URL = "http://localhost:8000";

class ApiService {
  constructor() {
    this.user = JSON.parse(localStorage.getItem("user")) || null;
  }

  async request(endpoint, options = {}) {
    const isAuthRoute = ["/login", "/logout", "/sanctum/csrf-cookie"].includes(
      endpoint
    );
    const baseUrl = isAuthRoute ? AUTH_BASE_URL : API_BASE_URL;

    const url = `${baseUrl}${endpoint}`;

    const config = {
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        ...options.headers,
      },
      credentials: "include", // Needed for Sanctum to send cookies
      ...options,
    };

    try {
      const response = await fetch(url, config);
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || "Une erreur s’est produite.");
      }

      return data;
    } catch (error) {
      throw error;
    }
  }

  // Step 1: Get CSRF cookie
  async getCsrfCookie() {
    await this.request("/sanctum/csrf-cookie", { method: "GET" });
  }

  // Step 2: Login
  async login(credentials) {
    const response = await this.request("/login", {
      method: "POST",
      body: JSON.stringify(credentials),
    });

    localStorage.setItem("user", JSON.stringify(response.user));
    this.user = response.user;

    return response;
  }

  // Logout
  async logout() {
    await this.request("/logout", { method: "POST" });
    localStorage.removeItem("user");
    this.user = null;
  }

  // Example: Authenticated API GET
  async getMyRides() {
    return await this.request("/my-rides", {
      method: "GET",
    });
  }

  // Example: Booking a ride
  async bookRide(bookingData) {
    return await this.request("/bookings", {
      method: "POST",
      body: JSON.stringify(bookingData),
    });
  }
}

export default new ApiService();
