} from "lucide-react";
import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import ApiService from "../services/api";

const ProfilePage = () => {
  const [user, setUser] = useState(null);
  const [userRides, setUserRides] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    loadUserData();
  }, []);

  const loadUserData = async () => {
    try {
      setLoading(true);

      // Get current user
      const userResponse = await ApiService.getCurrentUser();
      if (userResponse.success) {
        setUser(userResponse.data);
      }

      // Get user's rides/bookings
      const ridesResponse = await ApiService.getMyRides();
      if (ridesResponse.success) {
        setUserRides(ridesResponse.data.data || []);
      }
    } catch (error) {
      console.error("Error loading profile data:", error);
      setError("Erreur lors du chargement du profil");
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = async () => {
    try {
      await ApiService.logout();
      navigate("/");
    } catch (error) {
      console.error("Logout error:", error);
      // Force logout even if API call fails
      localStorage.removeItem("token");
      localStorage.removeItem("user");
      navigate("/");
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("fr-FR", {
      year: "numeric",
      month: "long",
      day: "numeric",
    });
  };

  const formatTime = (timeString) => {
    return new Date(`2000-01-01T${timeString}`).toLocaleTimeString("fr-FR", {
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Chargement du profil...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <p className="text-red-600 mb-4">{error}</p>
          <button
            onClick={loadUserData}
            className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
          >
            Réessayer
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="container mx-auto px-4 max-w-6xl">
        {/* Profile Header */}
        <div className="bg-white rounded-xl shadow-sm p-6 mb-8">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center">
                <User className="h-8 w-8 text-blue-600" />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-900">
                  {user?.name}
                </h1>
                <p className="text-gray-600">{user?.email}</p>
                <div className="flex items-center mt-1">
                  <span
                    className={`px-2 py-1 rounded-full text-xs font-medium ${
                      user?.role === "driver"
                        ? "bg-green-100 text-green-800"
                        : "bg-blue-100 text-blue-800"
                    }`}
                  >
                    {user?.role === "driver" ? "Conducteur" : "Passager"}
                  </span>
                  {user?.rating > 0 && (
                    <div className="flex items-center ml-3">
                      <Star className="h-4 w-4 text-yellow-400 fill-current" />
                      <span className="text-sm text-gray-600 ml-1">
                        {user.rating.toFixed(1)} ({user.total_ratings} avis)
                      </span>
                    </div>
                  )}
                </div>
              </div>
            </div>
            <div className="flex space-x-2">
              <button className="flex items-center space-x-2 px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50">
                <Edit className="h-4 w-4" />
                <span>Modifier</span>
              </button>
              <button
                onClick={handleLogout}
                className="flex items-center space-x-2 px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700"
              >
                <LogOut className="h-4 w-4" />
                <span>Déconnexion</span>
              </button>
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="grid md:grid-cols-3 gap-6 mb-8">
          <div className="bg-white rounded-xl shadow-sm p-6 text-center">
            <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <MapPin className="h-6 w-6 text-blue-600" />
            </div>
            <h3 className="font-semibold mb-2">Rechercher un trajet</h3>
            <Link
              to="/search"
              className="text-blue-600 hover:text-blue-700 font-medium"
            >
              Commencer →
            </Link>
          </div>

          {user?.role === "driver" && (
            <div className="bg-white rounded-xl shadow-sm p-6 text-center">
              <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <Car className="h-6 w-6 text-green-600" />
              </div>
              <h3 className="font-semibold mb-2">Publier un trajet</h3>
              <Link
                to="/publish"
                className="text-green-600 hover:text-green-700 font-medium"
              >
                Créer →
              </Link>
            </div>
          )}

          <div className="bg-white rounded-xl shadow-sm p-6 text-center">
            <div className="w-12 h-12 bg-purple-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <Calendar className="h-6 w-6 text-purple-600" />
            </div>
            <h3 className="font-semibold mb-2">Mes trajets</h3>
            <span className="text-purple-600 font-medium">
              {userRides.length} trajet{userRides.length !== 1 ? "s" : ""}
            </span>
          </div>
        </div>

        {/* Rides/Bookings Section */}
        <div className="bg-white rounded-xl shadow-sm p-6">
          <h2 className="text-xl font-bold mb-6">
            {user?.role === "driver"
              ? "Mes trajets publiés"
              : "Mes réservations"}
          </h2>

          {userRides.length === 0 ? (
            <div className="text-center py-12">
              <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <Car className="h-8 w-8 text-gray-400" />
              </div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                {user?.role === "driver"
                  ? "Aucun trajet publié"
                  : "Aucune réservation"}
              </h3>
              <p className="text-gray-600 mb-6">
                {user?.role === "driver"
                  ? "Commencez par publier votre premier trajet"
                  : "Recherchez et réservez votre premier trajet"}
              </p>
              <Link
                to={user?.role === "driver" ? "/publish" : "/search"}
                className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors"
              >
                {user?.role === "driver"
                  ? "Publier un trajet"
                  : "Rechercher un trajet"}
              </Link>
            </div>
          ) : (
            <div className="space-y-4">
              {userRides.map((item, index) => {
                // Handle both driver rides and passenger bookings
                const ride = item.ride || item;
                const isBooking = !!item.ride;

                return (
                  <div
                    key={index}
                    className="border rounded-lg p-6 hover:shadow-md transition-shadow"
                  >
                    <div className="flex justify-between items-start">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-2">
                          <span className="font-medium">{ride.from}</span>
                          <span className="text-gray-400">→</span>
                          <span className="font-medium">{ride.to}</span>
                        </div>

                        <div className="flex items-center gap-4 text-sm text-gray-600 mb-2">
                          <div className="flex items-center">
                            <Calendar className="h-4 w-4 mr-1" />
                            <span>{formatDate(ride.date)}</span>
                          </div>
                          <div className="flex items-center">
                            <Clock className="h-4 w-4 mr-1" />
                            <span>{formatTime(ride.time)}</span>
                          </div>
                          <div className="flex items-center">
                            <Car className="h-4 w-4 mr-1" />
                            <span>{ride.car}</span>
                          </div>
                        </div>

                        {isBooking && (
                          <div className="text-sm text-gray-600 mb-2">
                            <span>Conducteur: {ride.driver?.name}</span>
                            <span className="ml-4">
                              Places réservées: {item.seats_booked}
                            </span>
                          </div>
                        )}

                        {!isBooking && (
                          <div className="text-sm text-gray-600">
                            <span>
                              Places disponibles: {ride.available_seats}
                            </span>
                            {ride.bookings && ride.bookings.length > 0 && (
                              <span className="ml-4">
                                Réservations: {ride.bookings.length}
                              </span>
                            )}
                          </div>
                        )}
                      </div>

                      <div className="text-right">
                        <div className="text-2xl font-bold text-green-600 mb-2">
                          {isBooking ? item.total_price : ride.price}€
                        </div>
                        <div className="flex space-x-2">
                          <span
                            className={`px-2 py-1 rounded-full text-xs font-medium ${
                              (isBooking ? item.status : ride.status) ===
                              "active"
                                ? "bg-green-100 text-green-800"
                                : (isBooking ? item.status : ride.status) ===
                                  "completed"
                                ? "bg-blue-100 text-blue-800"
                                : "bg-red-100 text-red-800"
                            }`}
                          >
                            {isBooking ? item.status : ride.status}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
