"use client"

import { useEffect, useState } from "react"
import { useLocation } from "react-router-dom"
import RideCard from "../components/RideCard"
import SearchForm from "../components/SearchForm"

const SearchRides = () => {
  const location = useLocation()
  const [rides, setRides] = useState([])
  const [filters, setFilters] = useState({
    priceRange: [0, 100],
    departureTime: "",
    sortBy: "price",
    minSeats: 1,
    amenities: [],
    minRating: 0,
  })

  // Mock data for rides
  const mockRides = [
    {
      id: 1,
      driver: {
        name: "Marie Dupont",
        rating: 4.8,
        avatar: "",
      },
      from: "Paris",
      to: "Lyon",
      date: "2024-01-15",
      time: "08:00",
      price: 25,
      availableSeats: 3,
      totalSeats: 4,
      car: "Renault Clio",
      amenities: ["wifi", "music", "non-smoking"],
    },
    {
      id: 2,
      driver: {
        name: "Pierre Martin",
        rating: 4.5,
        avatar: "",
      },
      from: "Paris",
      to: "Lyon",
      date: "2024-01-15",
      time: "14:30",
      price: 30,
      availableSeats: 2,
      totalSeats: 4,
      car: "Peugeot 308",
      amenities: ["music", "air-conditioning"],
    },
    {
      id: 3,
      driver: {
        name: "Sophie Bernard",
        rating: 4.9,
        avatar: "",
      },
      from: "Paris",
      to: "Lyon",
      date: "2024-01-15",
      time: "18:00",
      price: 28,
      availableSeats: 1,
      totalSeats: 4,
      car: "Volkswagen Golf",
      amenities: ["wifi", "non-smoking", "pets-allowed"],
    },
  ]

  useEffect(() => {
    // Simulate API call
    setRides(mockRides)
  }, [])

  const handleNewSearch = (searchData) => {
    console.log("New search:", searchData)
  }

  const filteredRides = rides.filter((ride) => {
    // Price filter
    const isWithinPrice = ride.price >= filters.priceRange[0] && ride.price <= filters.priceRange[1]

    // Time filter
    const rideHour = parseInt(ride.time.split(":")[0])
    let isWithinTime = true
    if (filters.departureTime === "morning") {
      isWithinTime = rideHour >= 6 && rideHour < 12
    } else if (filters.departureTime === "afternoon") {
      isWithinTime = rideHour >= 12 && rideHour < 18
    } else if (filters.departureTime === "evening") {
      isWithinTime = rideHour >= 18 && rideHour < 24
    }

    // Seats filter
    const hasEnoughSeats = ride.availableSeats >= filters.minSeats

    // Amenities filter
    const hasSelectedAmenities = filters.amenities.length === 0 ||
      filters.amenities.every(amenity => ride.amenities.includes(amenity))

    // Rating filter
    const meetsRating = ride.driver.rating >= filters.minRating

    return isWithinPrice && isWithinTime && hasEnoughSeats && hasSelectedAmenities && meetsRating
  })

  const sortedRides = [...filteredRides].sort((a, b) => {
    switch (filters.sortBy) {
      case "price":
        return a.price - b.price
      case "time":
        return a.time.localeCompare(b.time)
      case "rating":
        return b.driver.rating - a.driver.rating
      default:
        return 0
    }
  })

  const handleAmenityChange = (amenity) => {
    setFilters(prev => ({
      ...prev,
      amenities: prev.amenities.includes(amenity)
        ? prev.amenities.filter(a => a !== amenity)
        : [...prev.amenities, amenity]
    }))
  }

  return (
    <div className="container py-4">
      <div className="row">
        {/* Search Sidebar */}
        <div className="col-lg-3 mb-4">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">Nouvelle recherche</h5>
            </div>
            <div className="card-body">
              <SearchForm onSearch={handleNewSearch} />
            </div>
          </div>

          <div className="card mt-3">
            <div className="card-header">
              <h5 className="mb-0">Filtres</h5>
            </div>
            <div className="card-body">
              <div className="mb-3">
                <label className="form-label">Trier par</label>
                <select
                  className="form-select"
                  value={filters.sortBy}
                  onChange={(e) => setFilters({ ...filters, sortBy: e.target.value })}
                >
                  <option value="price">Prix croissant</option>
                  <option value="time">Heure de départ</option>
                  <option value="rating">Note du conducteur</option>
                </select>
              </div>

              <div className="mb-3">
                <label className="form-label">Plage de prix</label>
                <div className="d-flex align-items-center">
                  <input
                    type="range"
                    className="form-range me-2"
                    min="0"
                    max="100"
                    value={filters.priceRange[0]}
                    onChange={(e) =>
                      setFilters({
                        ...filters,
                        priceRange: [Number.parseInt(e.target.value), filters.priceRange[1]],
                      })
                    }
                  />
                  <input
                    type="range"
                    className="form-range"
                    min="0"
                    max="100"
                    value={filters.priceRange[1]}
                    onChange={(e) =>
                      setFilters({
                        ...filters,
                        priceRange: [filters.priceRange[0], Number.parseInt(e.target.value)],
                      })
                    }
                  />
                </div>
                <div className="d-flex justify-content-between">
                  <small>{filters.priceRange[0]} TND</small>
                  <small>{filters.priceRange[1]} TND</small>
                </div>
              </div>

              <div className="mb-3">
                <label className="form-label">Heure de départ</label>
                <select
                  className="form-select"
                  value={filters.departureTime}
                  onChange={(e) => setFilters({ ...filters, departureTime: e.target.value })}
                >
                  <option value="">Toute la journée</option>
                  <option value="morning">Matin (6h-12h)</option>
                  <option value="afternoon">Après-midi (12h-18h)</option>
                  <option value="evening">Soir (18h-24h)</option>
                </select>
              </div>

              <div className="mb-3">
                <label className="form-label">Places minimum</label>
                <input
                  type="number"
                  className="form-control"
                  min="1"
                  max="4"
                  value={filters.minSeats}
                  onChange={(e) =>
                    setFilters({ ...filters, minSeats: Number.parseInt(e.target.value) || 1 })
                  }
                />
              </div>

              <div className="mb-3">
                <label className="form-label">Équipements</label>
                <div>
                  {["wifi", "music", "non-smoking", "pets-allowed", "air-conditioning"].map(amenity => (
                    <div key={amenity} className="form-check">
                      <input
                        type="checkbox"
                        className="form-check-input"
                        id={amenity}
                        checked={filters.amenities.includes(amenity)}
                        onChange={() => handleAmenityChange(amenity)}
                      />
                      <label className="form-check-label" htmlFor={amenity}>
                        {amenity.replace("-", " ").replace(/\b\w/g, c => c.toUpperCase())}
                      </label>
                    </div>
                  ))}
                </div>
              </div>

              <div className="mb-3">
                <label className="form-label">Note minimum du conducteur</label>
                <select
                  className="form-select"
                  value={filters.minRating}
                  onChange={(e) => setFilters({ ...filters, minRating: Number.parseFloat(e.target.value) })}
                >
                  <option value="0">Toutes</option>
                  <option value="4">4.0+</option>
                  <option value="4.5">4.5+</option>
                  <option value="4.8">4.8+</option>
                </select>
              </div>
            </div>
          </div>
        </div>

        {/* Results */}
        <div className="col-lg-9">
          <div className="d-flex justify-content-between align-items-center mb-4">
            <h2>Trajets disponibles</h2>
            <span className="text-muted">{sortedRides.length} trajet(s) trouvé(s)</span>
          </div>

          {sortedRides.length === 0 ? (
            <div className="text-center py-5">
              <i className="fas fa-search fa-3x text-muted mb-3"></i>
              <h4>Aucun trajet trouvé</h4>
              <p className="text-muted">Essayez de modifier vos critères de recherche</p>
            </div>
          ) : (
            <div className="row g-3">
              {sortedRides.map((ride) => (
                <div key={ride.id} className="col-12">
                  <RideCard ride={ride} />
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

export default SearchRides
